import React, { Component } from "react";
import PageHelmet from "../component/common/Helmet";
import Breadcrumb from "../elements/common/Breadcrumb";
import Testimonial from "../elements/Testimonial";
import ScrollToTop from 'react-scroll-up';
import { FiChevronUp } from "react-icons/fi";
import Header from "../component/header/Header";
import Footer from "../component/footer/Footer";
import PersonalNoteBg from "../../public/assets/images/no-excuse/personal-note-bg.png";


class NoExcuse extends Component{
    render(){
        let title = 'Jill of All Trades.',
        description = 'In my short life, I have experienced so much. I\'ve managed a $6 million territory for an international fashion brand. I\'ve lived in open gazebos and showered with tarantulas in Nicaragua. I\'ve even helped program all the brand websites for an online media publication reaching as many black millenials as we could. There is no paragraph that would dare summarize my entire life and professional experience, but if you\'re inspired by my work just wait until you see what I do next! Make sure you visit my services page to see how I can help you follow your curiosity into a branding experience you won\'t forget!',
        myHTML = `<div style="position: relative; width: 100%; height: 0; padding-top: 56.2500%; padding-bottom: 48px; overflow: hidden; border-radius: 8px; will-change: transform;"><iframe style="position: absolute; width: 100%; height: 100%; top: 0; left: 0; border: none; padding: 0;margin: 0;" src="https:&#x2F;&#x2F;www.canva.com&#x2F;design&#x2F;DAENxQ13VWU&#x2F;view?embed"></iframe></div>`,
        personalNoteStyle = {
            backgroundImage: "url(" + { PersonalNoteBg } + ")",
            backgroundSize: "auto",
            backgroundRepeat: "no-repeat",
            backgroundColor: "#F2F2F2"
        },
        buttonStyle = {
            backgroundColor: "#F4D345",
            color: "#3B2317",
            fontSize: "2em",
            fontWeight: "bold"
        };
        return(
            <React.Fragment>
                <PageHelmet pageTitle='No Excuse Branding & Website Kit' />

                {/* <Header headertransparent="header--transparent" colorblack="color--black" logoname="logo.png" /> */}
                {/* Start Breadcrump Area */}
                {/* <Breadcrumb title={'About'}   /> */}
                {/* End Breadcrump Area */}

                {/* <div dangerouslySetInnerHTML={{ __html: myHTML }} /> */}

                {/* Start Header Area  */}
                <div className="no-excuse-header">
                    <img src="/assets/images/no-excuse/No-Excuse-Header.png" alt="No Excuse Header"/>
                </div>

                {/* Start A Personal note Area  */}
                <div className="no-excuse-personal-note" style={ personalNoteStyle }>
                    <img src="/assets/images/no-excuse/personal-note-bg.png" style={{ zIndex: "0", position: "absolute", left: "0px", top: "810px" }} alt="Personal Note Background"/>
                    <div style={{ zIndex: "2", position: "absolute", left: "0px", top: "810px" }}>
                        <h1>A personal note from the designer</h1>
                        <h2>Candace Rogue</h2>
                        <h3>I don’t want you to wait as long as I did to build a web presence *cough cough* It was 4 years.</h3>
                        <p>Because someone NEEDS exactly what you have to offer</p>
                        <p>Whether that’s consulting services, a subscription box or groundbreaking-product. They need you now.</p>
                        <p>Don’t you want to get out of your own way? Stop waiting until you take that one class that makes you an “expert”, or the perfect photographer to take your headshots/product pictures. Someone could see that IG Live where you were spittin’ hot fire and want to buy the service NOW.</p>
                        <p>Website up and product/service offerings ready to buy? Start HERE with this kit.</p>
                        <p>I want you to start pacing toward your 7 figure dreams today.</p>
                        <p>And that is why I designed this kit.</p>
                        <h3>No more waiting. No more bullshit. Say “goodbye” to the excuses and “hello” to your no nonsense, money making landing page.</h3>
                        <button style={ buttonStyle }>You Ready?</button>
                    </div>
                </div>
                <br/>
                <br/>
                {/* Start Back To Top */}
                <div className="backto-top">
                    <ScrollToTop showUnder={160}>
                        <FiChevronUp />
                    </ScrollToTop>
                </div>
                {/* End Back To Top */}

                <Footer />

            </React.Fragment>
        )
    }
}
export default NoExcuse
